mapper.update:
UPDATE tb_board
SET
	  UPDT_DATE = NOW()
	, ACAD_CD = #{ACAD_CD}
	, TITLE = #{TITLE}
	, CONTENT = #{CONTENT}
WHERE
	BOARD_NO = #{BOARD_NO}

